css:
.github-CommitDetailView {
  display: flex;
  flex-direction: column;
  height: 100%;
}
.github-CommitDetailView-header {
  flex: 0;
  border-bottom: 1px solid #20252a;
  background-color: #1D252C;
}
.github-CommitDetailView-commit {
  padding: 2rem;
  padding-bottom: 0;
}
.github-CommitDetailView-title {
  margin: 0 0 .25em 0;
  font-size: 1.4em;
  line-height: 1.3;
  color: #d3dce4;
}
.github-CommitDetailView-avatar {
  border-radius: 4px;
  height: 16px;
  margin-right: .4em;
  width: 16px;
}
.github-CommitDetailView-meta {
  display: flex;
  align-items: center;
  margin: 0.5rem 0 2rem 0;
}
.github-CommitDetailView-metaText {
  flex: 1;
  margin-left: 22.4px;
  line-height: 16px;
  color: #798c9d;
}
.github-CommitDetailView-moreButton {
  position: absolute;
  left: 50%;
  transform: translate(-50%, -50%);
  padding: 0em .4em;
  color: #798c9d;
  font-style: italic;
  border: 1px solid #20252a;
  border-radius: 4px;
  background-color: #3a444d;
}
.github-CommitDetailView-moreButton:hover {
  background-color: #2d353b;
}
.github-CommitDetailView-moreText {
  padding: 2rem 0;
  font-size: inherit;
  font-family: var(--editor-font-family);
  word-wrap: initial;
  word-break: break-word;
  white-space: pre-wrap;
  border-top: 1px solid #20252a;
  background-color: transparent;
  max-height: 55vh;
}
.github-CommitDetailView-moreText:empty {
  display: none;
}
.github-CommitDetailView-sha {
  flex: 0 0 7ch;
  margin-left: 2rem;
  line-height: 16px;
  color: #798c9d;
  font-family: var(--editor-font-family);
  white-space: nowrap;
  overflow: hidden;
}
.github-CommitDetailView-sha a {
  color: #5cadd6;
}
